package asymkey

import (
	"testing"

	asymkey_model "code.gitea.io/gitea/models/asymkey"
	"code.gitea.io/gitea/models/auth"
	"code.gitea.io/gitea/models/db"
	"code.gitea.io/gitea/models/unittest"
	user_model "code.gitea.io/gitea/models/user"

	"github.com/stretchr/testify/assert"
)

func TestAddLdapSSHPublicKeys(t *testing.T) {
	assert.NoError(t, unittest.PrepareTestDatabase())

	user := unittest.AssertExistsAndLoadBean(t, &user_model.User{ID: 2})
	s := &auth.Source{ID: 1}

	testCases := []struct {
		keyString   string
		number      int
		keyContents []string
	}{
		{
			keyString: "ssh-rsa  nocomment\n",
			number:    1,
			keyContents: []string{
				"ssh-rsa ",
			},
		},
		{
			keyString: `ssh-rsa  nocomment
ssh-dss  nocomment`,
			number: 2,
			keyContents: []string{
				"ssh-rsa ",
				"ssh-dss ",
			},
		},
		{
			keyString: `ssh-rsa  nocomment
# comment asmdna,ndp
ssh-dss  nocomment`,
			number: 2,
			keyContents: []string{
				"ssh-rsa ",
				"ssh-dss ",
			},
		},
		{
			keyString: `ssh-rsa  nocomment
382488320jasdj1lasmva/vasodifipi4193-fksma.cm
ssh-dss  nocomment`,
			number: 2,
			keyContents: []string{
				"ssh-rsa ",
				"ssh-dss ",
			},
		},
	}

	for i, kase := range testCases {
		s.ID = int64(i) + 20
		asymkey_model.AddPublicKeysBySource(db.DefaultContext, user, s, []string{kase.keyString})
		keys, err := asymkey_model.ListPublicKeysBySource(db.DefaultContext, user.ID, s.ID)
		assert.NoError(t, err)
		if err != nil {
			continue
		}
		assert.Len(t, keys, kase.number)

		for _, key := range keys {
			assert.Contains(t, kase.keyContents, key.Content)
		}
		for _, key := range keys {
			DeletePublicKey(db.DefaultContext, user, key.ID)
		}
	}
}
